import React, { useEffect, useState } from "react";
import '../Styles/RecipeCard.css';

export default function RecipeCard() {
  const [api, setApi] = useState([]);

  const fetchApi = async () => {
    try {
      const response = await fetch("https://raw.githubusercontent.com/Harshal260204/harshalApi/refs/heads/main/db.json");
      const apidata = await response.json(); // Fix: Await for response.json()
      setApi(apidata);
      console.log(apidata);
    } catch (error) {
      console.error("Error fetching API:", error);
    }
  };

  useEffect(() => {
    fetchApi();
  }, []);

  return (
    <div className="recipe-container">
      {api.map((data) => (
        <div className="card m-2" style={{ width: "18rem" }} key={data.id}>
          <img
            src={data.img}
            className="card-img-top"
            alt={data.name}
          />
          <div className="card-body">
            <h5 className="RecipeName">{data.name}</h5>
            <h6 className="RecipeType">{data.price || "Price not available"}</h6>
            <p className="card-text">{data.dsc}</p>
            <a href="#" className="btn btn-primary">
              View Detailed Recipe
            </a>
          </div>
        </div>
      ))}
    </div>
  );
}
